import React from 'react';
import './App.css';
import axios from 'axios';
import Weather from './Components/Weather/Weather';

class App extends React.Component {
  state = {
    location: []
  }

  userInput = () => {
    let input = document.getElementById('searchInput').value;
    axios.get(`http://localhost:8080/location/search/?query=${input}`)
    .then(res => {
      this.setState({location: res.data[0]})
    })
    .catch(err => console.log(err));
    this.weather()

  }


  render(){
  return (
    <div className="App">
      <input id="searchInput"></input>
      <button onClick={ (e) => {e.preventDefault(); this.userInput(); {<Weather woeid={this.state.location.woeid} />}}}>Search</button>
      <h1>{this.state.location.title}</h1>
      <span>{this.weather()}</span>
    </div>
  );
  }
}


export default App;
