import React from 'react';
import './App.css';
import axios from 'axios';
import Weather from './Components/Weather/Weather';

class App extends React.Component {
  state = {
    location: []
  }

  userInput = (e) => {
    e.preventDefault();
    let input = document.getElementById('searchInput').value;
    axios.get(`http://localhost:8080/location/search/?query=${input}`)
    .then(res => {
      this.setState({location: res.data[0]})
    })
    .catch(err => console.log(err))

  }

  weather = () => {
    {if(this.state.location.woeid){
      return (
        <Weather woeid={this.state.location.woeid} />
      )
  }}
  }

  render(){
  return (
    <div className="App">
      <input id="searchInput"></input>
      <button onClick={this.userInput}>Search</button>
      <h1>{this.state.location.title}</h1>
      <span>{this.weather()}</span>
    </div>
  );
  }
}


export default App;
